    public void TestChk()
    {
        if (m_NullBagItems.Count < inventorySize)
        {
            // 새로 만들어서 그리드 자식으로 넣음
            GameObject testChkBox = NGUITools.AddChild(nullBag.gameObject, testChk);
            // 관리를 위해 만든걸 리스트에 넣어둠
            ItemScript itemScript = testChkBox.GetComponent<ItemScript>();
            m_NullBagItems.Add(itemScript);

            // 그리드와 스크롤뷰를 재정렬
            nullBag.Reposition();
            m_scrollView.ResetPosition();
        }

        if (m_NullBagItems.Count > inventorySize)
        {
            Debug.Log("인벤 full . 더 이상 가질 수 없습니다.");
        }
    }

    public void TestMakeItem()
    {
        if (m_NullBagItems.Count < inventorySize && chkBoxItemEa == 0)
        {
            // 새로 만들어서 그리드 자식으로 넣음
            GameObject boxSampleItem = NGUITools.AddChild(nullBag.gameObject, testInstansBox);
            // 관리를 위해 만든걸 리스트에 넣어둠
            ItemScript itemScript = boxSampleItem.GetComponent<ItemScript>();
            m_NullBagItems.Add(itemScript);

            // 그리드와 스크롤뷰를 재정렬
            nullBag.Reposition();
            m_scrollView.ResetPosition();
            chkBoxItemEa += 1;
        }
        else
        if (m_NullBagItems.Count < inventorySize && chkBoxItemEa >= 1 && chkBoxItemEa < itemStackSize)
        {
            Debug.Log("중첩 하자");
            chkBoxItemEa += 1;
            GameObject box = m_NullBagItems.Find(x => x.name == "TEST_CAST").gameObject;
            box.GetComponent<ItemScript>().m_equipLabel.gameObject.SetActive(true);
            if (box.GetComponent<ItemScript>().m_equipLabel.gameObject.activeSelf == true)
            {
                box.GetComponent<ItemScript>().m_equipLabel.text = chkBoxItemEa.ToString();
            }
        }

        if (m_NullBagItems.Count > inventorySize)
        {
            Debug.Log("인벤 full . 더 이상 가질 수 없습니다.");
        }
    }

