    //상자 오픈
    public void UseBtnClick()
    {
        if (m_WeaponBagItems.Count < inventorySize && m_ArmorBagItems.Count < inventorySize
            && m_AccessoryBagItems.Count < inventorySize)
        {
            Debug.Log("상자 오픈");            
            RandomItemChange();
            ranManager.GetComponent<ItemRandom>().OutputClear();

            for (int i = 0; i < saveBoxData.Count; i++)
            {
                if (curItemScript.itemDataJson == saveBoxData[i])
                {
                    //saveItemData[i] = "";
                    //PlayerPrefs.DeleteKey("InvenItem" + i);
                    int nLast = saveBoxData.Count - 1;
                    PlayerPrefs.DeleteKey("InvenBoxItem" + nLast);
                    saveBoxData.RemoveAt(i);
                    PlayerPrefs.SetInt("InvenBoxItemNum", saveBoxData.Count);
                    for (int j = 0; j < saveBoxData.Count; j++)
                    {
                        PlayerPrefs.SetString("InvenBoxItem" + j, saveBoxData[j]);
                    }
                    BoxNum--;
                    break;
                }
            }
        }
        else
        {
            bagItemPopup.SetActive(false);
            StartCoroutine("InvenFullMessage");
        }
    }

