    //상자 저장
    void BoxSave()
    {
        BoxData BoxData = new BoxData();
        var BoxItemJson = JsonUtility.ToJson(BoxData); //var InvenItemJson를 위의 클래스 json으로 변환
        PlayerPrefs.SetString("InvenBoxItem" + BoxNum, BoxItemJson.ToString()); //json 스트링 변환
        saveBoxData.Add(BoxItemJson.ToString());
        //Debug.Log(InvenItemJson);
        //Debug.Log(InvenItemJson.ToString());
        //Debug.Log("저장된 아이템 번호 : " + itemNum);
        BoxNum++;
        PlayerPrefs.SetInt("InvenBoxItemNum", BoxNum);
        //Debug.Log("다음으로 저장될 아이템 번호 : " + itemNum);
        Debug.Log("박스 저장");
        itemDataJson = BoxItemJson.ToString();
    }

    //상자 불러오기
    void BoxLaod()
    {
        BoxNum = PlayerPrefs.GetInt("InvenBoxItemNum");
        for (int i = 0; i < BoxNum; i++)
        {
            //리스트에 json에서 리스트 로바꾼거를 애드.리스트 사이즈 숫자도 정해줌 
            saveBoxData.Add(PlayerPrefs.GetString("InvenBoxItem" + i));

            // saveItemData에 처음 json에서 string으로 바꾼 정보를 다시 var SaveItemData 에 json으로 저장
            var SaveBoxData = JSON.Parse(saveBoxData[i]);

            //상자
            if (SaveBoxData["ItemName"] == "Box")
            {
                GameObject RanBox = NGUITools.AddChild(nullBag.gameObject, tsetRandomBox);

                ItemScript itemScript = RanBox.GetComponent<ItemScript>();

                itemScript.ItemName = SaveBoxData["ItemName"];
                itemScript.GradeName = SaveBoxData["GradeName"];

                itemScript.itemDataJson = SaveBoxData.ToString();

                // 선언 된 스크립트를 해당 가방 리스트에 추가        
                m_NullBagItems.Add(itemScript);
            }
        }
    }


